 EXISTS ( SELECT 1 FROM  (SELECT UID_Person FROM Person WHERE  EXISTS ( SELECT 1 FROM  (SELECT UID_Department FROM Department WHERE  EXISTS ( SELECT 1 FROM  (SELECT UID_Department FROM Department WHERE  EXISTS ( SELECT 1 FROM  (SELECT UID_Person FROM Person WHERE 1 = 1) as X  WHERE X.UID_Person = Department.UID_PersonHead  ) ) as X  WHERE X.UID_Department = Department.UID_ParentDepartment  ) ) as X  WHERE X.UID_Department = Person.UID_Department  ) ) as X  WHERE X.UID_Person = PersonWantsOrg.UID_PersonOrdered  )